package com.class15;

public class StringDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		String str=new String("hi");
		String str1="hi";
		System.out.println(str1.length());//counts whats inside the ""  =2
		
		
		System.out.println("...example #2....");
		
		String str2="Hi how are you";
		System.out.println(str2.length());   //counts the characters in" " including spaces

		System.out.println();
		System.out.println("...example #3....");
		
		String str3="HI HOW ARE YOU";
		System.out.println(str3.toLowerCase());// all lower case inside " "
		
		
		
		
		
	}

}
